package com.glasswallet.platform.service;

import com.glasswallet.platform.data.models.PlatformUser;
import com.glasswallet.platform.data.repositories.PlatformUserRepository;
import com.glasswallet.platform.dtos.requests.PlatformUserRequest;
import com.glasswallet.user.data.models.User;
import com.glasswallet.user.data.repositories.UserRepository;
import com.glasswallet.user.exceptions.UserNotFoundException;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;
import org.apache.commons.lang3.RandomStringUtils;


@Slf4j
@Service
@RequiredArgsConstructor
public class PlatformUserService {

    private final UserRepository userRepository;
    private final PlatformUserRepository platformUserRepository;

    @Transactional
    public PlatformUser onboardPlatformUser(PlatformUserRequest request) {
        User user = userRepository.findByEmail(request.getEmail())
                .orElseGet(() -> {
                    User newUser = new User();
                    newUser.setEmail(request.getEmail());
                    newUser.setFirstName(request.getFirstName());
                    newUser.setLastName(request.getLastName());
                    newUser.setPhoneNumber(request.getPhoneNumber());
                    newUser.setUsername(request.getFirstName(), request.getLastName());

                    // ✅ Generate a secure random password
                    String generatedPassword = RandomStringUtils.randomAlphanumeric(10);
                    newUser.setPasswordHash(passwordEncoder.encode(generatedPassword));
                    User savedUser = userRepository.save(newUser);

                    // ✅ Send SMS or email (stub)
                    sendWelcomeNotification(savedUser, generatedPassword);

                    return savedUser;
                });

        if (platformUserRepository.existsByCompanyIdAndCompanyUserId(request.getCompanyId(), request.getCompanyUserId())) {
            throw new DuplicatePlatformUserException("Platform user already exists for this company and external ID");
        }

        PlatformUser platformUser = new PlatformUser();
        platformUser.setCompanyId(request.getCompanyId());
        platformUser.setCompanyUserId(request.getCompanyUserId());
        platformUser.setUser(user);

        return platformUserRepository.save(platformUser);
    }

    private void sendWelcomeNotification(User user, String rawPassword) {
        log.info("Sending welcome message to {} with temporary password: {}", user.getEmail(), rawPassword);
        // ✅ Replace with actual SMS or Email service
    }


    public User getUserByPlatformUserId(String companyId, String companyUserId) {
        return platformUserRepository.findByCompanyIdAndCompanyUserId(companyId, companyUserId)
                .orElseThrow(() -> new UserNotFoundException("Platform user mapping not found"))
                .getUser();
    }
}

